import React, {useRef, useState, useEffect} from 'react'
import { GrNotes } from 'react-icons/gr'
import { GoNote } from 'react-icons/go'
import { VscGithubAction } from 'react-icons/vsc'
import axios from 'axios'
import DescriptionForm from './Note/DescriptionForm'

function NoteDetails(props) {

    let { _id, title, description } = props.note;
    const [mouseLeave, setMouseLeave] = useState(true)
    const [noteDescription, setNoteDescription] = useState(description)
    const [noteDescriptionFokus, setNoteDescriptionFokus] = useState(false)
    const ref = useRef()
    //////// FUNCTIONS

    useEffect( () => {
        const getNotes = async () => {
            const notes = await axios.get(`/boards/${props.boardId}`)
            
        }
        
        
       
    },[] )

    const handleMouseLeaveClick = () => {
        if(mouseLeave) ref.current.addEventListener('click', props.setShowDetails(false))
    }

    const handlePatchDescription = async () => {
        const notes = await axios.get(`boards/${props.boardId}`)
        const updatedNotes = notes.data.content.filter( note => {
            if(note._id !== _id) {console.log('1'); return note}
            else {  note.desc = noteDescription; return note}
        } )
        
        
        
        
    }

    
    return (
        <div ref = {ref} onClick = {handleMouseLeaveClick}  className  = 'note-details'>
            <div onMouseEnter = {() => {setMouseLeave(false)}} onMouseLeave = {() => {setMouseLeave(true)}}  className='note-details__wrapper'>
                <div className='note-details__top'>
                    <GrNotes className='note-details__icon' />
                    <div className='note-details__header__wrapper'>
                        <h3 className='note-details__header'  >{title}</h3>
                        <p>na liście: <span>{props.boardTitle}</span></p>
                    </div>
                </div>

                <div className='note-detaild__middle' >
                    <GoNote className='note-details__icon' />
                    <div className='note-details__details__wrapper' >
                        <h4>Opis</h4>
                        <div className='note-details__details__description'>
                        {noteDescriptionFokus ? <DescriptionForm description = {noteDescription} setNoteDescription = {setNoteDescription} boardId = {props.boardId}  _id = {_id} /> : 
                        
                        <form >
                            <textarea onClick = {() => setNoteDescriptionFokus(true)}  onChange = {(e) => setNoteDescription(e.target.value)}  value = {noteDescription} />
                         </form> }

                         
                        </div>
                    </div>
                </div>

                <div className='note-details__bottom' >
                    <VscGithubAction className='note-details__icon' />
                    <div className='note-details__comments' >
                        <h4>Aktywność</h4>
                        <form onSubmit = {handlePatchDescription}>
                            <input className='comment-input' placeholder='napisz komentarz' />
                        </form>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default NoteDetails
