import React from 'react'

function Comment(props) {

    const { content } = props.comment

    const commentWrapperStyle = {
        marginTop: '20px'
    };
    const commentTopStyle = {
        display: 'flex',
        padding: '5px 0',
        alignItems: 'center',
        marginBottom: '5px'

    }

    return (
        <div style={commentWrapperStyle}>
            <div style = {{display: 'flex', justifyContent: 'center', alignItems: 'center', backgroundColor: 'blue', width: '50px', height: '50px', borderRadius: "50%", color: 'white'}} ><span>G</span></div>
            <div>
                <div style={commentTopStyle}>
                    <p style={{ marginRight: '7px', fontWeight: '600' }} >Guest</p>
                    <p style={{ fontSize: '13px' }}>15:06 <span style={{ marginLeft: '6px' }} >26.04.2021</span></p>
                </div>
                <div>
                    <p style={{ padding: '5px', backgroundColor: 'white', borderRadius: '5px' }} >{content}</p>
                </div>
            </div>

        </div>
    )
}

export default Comment
