import React, { useState, useEffect } from 'react'
import Note from '../Note'
import './board.css'
import axios from 'axios'

function Board(props) {
    const { _id, title, content } = props.board

    const [notes, setNotes] = useState([])
    const [noteTitle, setNoteTitle] = useState()
    const [test, setTest] = useState(content)



    const createNote = async () => {
        axios.post('/notes', { title: noteTitle })
            .then(data => { data && axios.patch(`/boards/createNote/${_id}`, { content: [...test, data.data] }); setTest([...test, data.data]) })


    }

    const deleteNote = async (id) => {
        const filteredNotes = test.filter(note => note._id !== id)
        await axios.patch('./boards/createNote/ ' + _id, { content: filteredNotes })
        setTest(filteredNotes)
    }








    const handleAddNote = () => {
        return (
            <div>
                <input onChange={e => setNoteTitle(e.target.value)} />
                <button onClick={createNote} >add</button>
            </div>
        )
    }



    const renderNotes = () => {
        return (
            <div>
                {test && test.map(note => <Note deleteNote={deleteNote} note={note} />)}
            </div>
        )
    }


    return (
        <div className='board' >
            <h3>{_id}</h3>
            {renderNotes()}
            {handleAddNote()}
        </div>
    )
}

export default Board
