import React, {useState, useEffect} from 'react'
import Note from '../Note'
import './board.css'
import axios from 'axios'

function Board(props) {
    const{_id, title, content} = props.board

    const [board, setBoard] = useState()
    const [noteTitle, setNoteTitle] = useState()

    const fetchNotes = async () => {
        const notes = await axios.get('/notes')
        setBoard(notes.data)
    }
    

    useEffect(() => {
        fetchNotes()
    }, [])

   console.log(_id)


    const createNote = async () => {
        const note = {
            title: noteTitle
        }
        
        axios.post('/notes', note).then( res => setBoard([...board, res.data])).then(res => axios.post('/boards', res))
    }

    console.log(board) 

    const handleAddNote = () => {
        return(
            <div>
                <input onChange = {e => setNoteTitle(e.target.value) } />
                <button onClick = {createNote} >add</button>
            </div>
        )
    }

   

    const renderNotes = () => {
        return(
            <div>
                { board && board.map( note => <Note board = {board} setBoard = {setBoard} note = {note} /> )}
            </div>
        )
    }


    return (
        <div className = 'board' >
            <h1>{_id}</h1>
            {renderNotes()}
            {handleAddNote()}

            
        </div>
    )
}

export default Board
