import React, { useState, useEffect } from 'react'
import Note from '../Note'
import './board.css'
import axios from 'axios'
import { BsPlus } from 'react-icons/bs'

function Board(props) {
    const { _id, title, content } = props.board

    const [notes, setNotes] = useState([])
    const [boardTitle, setBoardTitle] = useState(title)
    const [noteTitle, setNoteTitle] = useState()
    const [test, setTest] = useState()

    useEffect(() => {
        setTest(content)
    }, [])

    const createNote = async () => {
        const newNote = await axios.post('/notes', { title: noteTitle })
        await setTest([...test, newNote.data])
        await axios.patch(`/boards/createNote/${_id}`, { content: [...test, newNote.data] })
    }

    const deleteNote = async (id) => {
        const filteredNotes = test.filter(note => note._id !== id)
        await axios.patch(`/boards/createNote/${_id}`, { content: filteredNotes })
        setTest(filteredNotes)
    }

    const patchBoardTitle = async () => {
        await axios.patch(`/boards/updateTitle/${_id}`, { title: boardTitle })
    }

    const handleAddNote = () => {
        return (
            <div>

                <form className='add-card__form'>
                    <BsPlus />
                    <input className='add-card__input' placeholder={'add card'} onChange={e => setNoteTitle(e.target.value)} />
                    <button onClick={createNote} >add</button>
                </form>
            </div>
        )
    }



    const renderNotes = () => {
        return (
            <div>
                {test && test.map(note => <Note boardTitle={boardTitle} deleteNote={deleteNote} note={note} />)}
            </div>
        )
    }


    return (
        <div className='board' >
            <div className='boardInput'>
                <input onChange={(e) => { setBoardTitle(e.target.value) }} value={boardTitle} />
            </div>
            {renderNotes()}
            {handleAddNote()}
        </div>
    )
}

export default Board
