import React, { useState, useEffect, useRef } from 'react'
import Note from '../Note'
import './board.css'
import axios from 'axios'
import { BsPlus } from 'react-icons/bs'

function Board(props) {
    const { _id, title, } = props.board
    const [board, setBoard] = useState()
    const [boardTitle, setBoardTitle] = useState(title)
    const [noteTitle, setNoteTitle] = useState()
    const [test, setTest] = useState()
    const ref = useRef()
    
    const fetchNotes = async () => {
        const notes = await axios.get(`/boards/${_id}`)
        setTest(notes.data.content)
    }

    useEffect(() => {
        fetchNotes()
    }, [])

    useEffect(() => {
        const patchBoard = async () => {
            await axios.patch(`/boards/updateTitle/${_id}`, {title : board.title} )
        }

        patchBoard()
    }, [board])


    const createNote = async () => {
        
        const newNote = await axios.post('/notes', {title: noteTitle})
        const res = await axios.patch(`/boards/createNote/${_id}`, { content: [...test, newNote.data]})
        setTest([...test, newNote.data])
        
    }

    const deleteNote = async (id) => {
        const filteredNotes = test.filter(note => note._id !== id)
        await axios.patch(`/boards/createNote/${_id}`, { content: filteredNotes })
        setTest(filteredNotes)
    }

    const patchBoardTitle =  (e) => {
       
        e.preventDefault()
        setBoard({...board, title : boardTitle})
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        createNote()
        ref.current.reset()
        return false
    }

    const handleAddNote = () => {
        return (
            <div>

                <form ref = {ref} onSubmit = { (e) => {handleSubmit(e); }  } className='add-card__form'>
                    <BsPlus />
                    <input  className='add-card__input' placeholder={'add card'} onChange={e => setNoteTitle(e.target.value)} />
                    <button  onClick={createNote} >add</button>
                </form>
            </div>
        )
    }

    

    const renderNotes = () => {
        return (
            <div>
                {test && test.map(note => <Note boardId = {_id} boardTitle={boardTitle} deleteNote={deleteNote} note={note} />)}
            </div>
        )
    }


    return (
        <div className='board' >
            <div className='boardInput'>
                <form onSubmit={ e => patchBoardTitle() }>
                    <input onChange={(e) => { setBoardTitle(e.target.value) }} value={boardTitle} />
                </form>
            </div>
            {renderNotes()}
            {handleAddNote()}
        </div>
    )
}

export default Board
