import React, {useState, useEffect} from 'react'
import Note from '../Note'
import './board.css'
import axios from 'axios'

function Board(props) {
    const{_id, title, content} = props.board

    const [board, setBoard] = useState(content)
    const [noteTitle, setNoteTitle] = useState()

    
    const createNote = async () => {
        await axios.patch('/boards/' + _id, {title: noteTitle} )
    }

  

    

   

    console.log(board) 

    const handleAddNote = () => {
        return(
            <div>
                <input onChange = {e => setNoteTitle(e.target.value) } />
                <button onClick = {createNote} >add</button>
            </div>
        )
    }

   

    const renderNotes = () => {
        return(
            <div>
                { board && board.map( note => <Note board = {board} setBoard = {setBoard} note = {note} /> )}
            </div>
        )
    }


    return (
        <div className = 'board' >
            <h1>{_id}</h1>
            {renderNotes()}
            {handleAddNote()}

            
        </div>
    )
}

export default Board
