import React, {useState, useEffect} from 'react'
import './Note.css'
import axios from 'axios'
import NoteDetails from './NoteDetails'


function Note(props) {
    const {_id, title, content} = props.note
    const [showDetails, setShowDetails] = useState(false)
    
    const  handleShowDetails = () => {
        if(showDetails === true) return <NoteDetails note = {props.note} />
    }

    useEffect(() => {
        handleShowDetails()
        console.log(showDetails)
    }, [showDetails])

   


    return (
        <div className = 'note'>
            <div onClick = {() => setShowDetails(!showDetails)}  className = 'note__content'>
                <input value = {title} />
                <button onClick = {() => {props.deleteNote(_id)}} > delete</button>
            </div>

            {showDetails ? handleShowDetails() : ''}
            

        </div>
    )
}

export default Note
