import React, {useState, useEffect, useRef} from 'react'
import './Home.css'
import Board from '../components/board/Board'
import axios from 'axios'
import BottomBar from '../components/BottomBar/BottomBar'
import { DragDropContext } from 'react-beautiful-dnd'

function Home() {
    const [boards, setBoards] = useState([])
    const boardsRef = useRef(boards)
    const fetchBoards = async () => {
        const boards = await axios.get('/boards')
        setBoards(boards.data)
    }
    

    useEffect(() => { 
        fetchBoards()
    }, [])

    useEffect(() => {
        boardsRef.current = boards
    }, [boards])
    console.log(boards)

    const handleOnDragEnd =  async (result) => {
        console.log(result)

        if (!result.destination) return;
        
        if (result.source.droppableId !== result.destination.droppableId) {
            // const source = test.find(column => column.id == result.source.droppableId)
            // const dest = test.find(column => column.id == result.destination.droppableId)
            // const [removed] = source.data.splice(result.source.index, 1)
            // dest.data.splice(result.destination.index, 0, removed)
            // console.log(removed, dest, test[1])
         
        }

        else {
            // const source = test
            // const [reorderedItem] = source.splice(result.source.index, 1)
            // source.splice(result.destination.index, 0, reorderedItem)
            // setBoard({
            //     ...board, content: source
            // })


            const board = boards.find(board => board._id == result.source.droppableId)
            const source = board.content
            const [reorderedItem] = source.splice(result.source.index, 1)
            source.splice(result.destination.index, 0, reorderedItem)
            const newArray = boards.map( item => {
                if(board._id !== item._id) {return item}
                else{ return {...item, content : source} }
            }  )
            console.log(newArray)
            setBoards([...newArray])
            // await axios.patch(`boards/createNote/${board._id}`, {content: source})
            
            
        }
    }


    const renderBoards = () => {
        return <div className = 'boards__wrapper'>
            <DragDropContext onDragEnd = {handleOnDragEnd}>
                {boards.map(board => <Board boards = {boards} setBoards = {setBoards} board = {board}/>)}
            </DragDropContext>
        </div>
            
        
    }

    return (
        <div className = 'home'>
            {renderBoards()}
            <BottomBar boards = {boards} setBoards = {setBoards} />
        </div>
        
    )
}

export default Home
