import React, {useState, useEffect} from 'react'
import Note from '../Note'
import './board.css'
import axios from 'axios'

function Board(props) {
    const{_id, title, content} = props.board

    const [notes, setNotes] = useState([])
    const [noteTitle, setNoteTitle] = useState()
    const [test, setTest] = useState(content)
    
    

    const createNote = () => {
        axios.post('/notes', {title: noteTitle})
        .then( res => setTest([...test, res.data]))
        .then( data => data && axios.patch('./boards/createNote/' + _id, { content : [...test, data.data]}) )
    }

    

  
    

   

    

    const handleAddNote = () => {
        return(
            <div>
                <input onChange = {e => setNoteTitle(e.target.value) } />
                <button onClick = {createNote} >add</button>
            </div>
        )
    }

   

    const renderNotes = () => {
        return(
            <div>
                { test && test.map( note => <Note  note = {note} /> )}
            </div>
        )
    }


    return (
        <div className = 'board' >
            <h1>{_id}</h1>
            {renderNotes()}
            {handleAddNote()}

            
        </div>
    )
}

export default Board
