import React, {useState, useEffect} from 'react'
import './Note.css'
import axios from 'axios'
import NoteDetails from './NoteDetails'


function Note(props) {
    const {_id, title, description } = props.note
    const [note, setNote] = useState()
    const [showDetails, setShowDetails] = useState(false)
    const [noteDesc, setNoteDesc] = useState('')

    useEffect(() => {
        setNote(props.note);
        setNoteDesc(props.note.description)
    }, [])



    const  handleShowDetails = () => {
        if(showDetails === true) return <NoteDetails noteDesc ={noteDesc} setNoteDesc = {setNoteDesc} handlePatchDescription = {handlePatchDescription} boardId = {props.boardId} showDetails = {showDetails} setShowDetails = {setShowDetails} boardTitle = {props.boardTitle} note = {props.note} />
    }




    const handlePatchDescription = async (e) => {
        e.preventDefault()
        const notes = await axios.get(`boards/${props.boardId}`)
        const updatedNotes = notes.data.content.filter( item => {
            if(item._id === note._id) { return {title: 'dupa'}}
            
        } )
        
        await axios.patch(`/boards/createNote/${props.boardId}`, {content : updatedNotes})
        console.log(updatedNotes)
    }
   


    return (
        <div className = 'note'>
            <div onClick = {() => setShowDetails(!showDetails)}  className = 'note__content'>
                <input value = {title} />
                <button onClick = {() => {props.deleteNote(_id)}} > delete</button>
            </div>

            {showDetails ? handleShowDetails() : ''}
            

        </div>
    )
}

export default Note
