import React, {useState, useEffect} from 'react'
import axios from 'axios'

function DescriptionForm(props) {

    const [description, setDescription] = useState()

    useEffect(() => {
        console.log(props.note)
    }, [])

    const handlePatchDescription = async (e) => {
        e.preventDefault()
        const notes = await axios.get(`boards/${props.boardId}`)
        const updatedNotes = notes.data.content.filter( note => {
            if(note._id !== props._id) { return note}
            else {  note.description = description; return note}
        } )
        console.log(updatedNotes)
        axios.patch(`/boards/createNote/${props.boardId}`, {content : updatedNotes})

    }
       

    return (
        <div >
            <form onSubmit={handlePatchDescription}>
                <textarea  onChange = {(e) => setDescription(e.target.value)} value = {description} ></textarea>
                <button>dodaj</button>
            </form>
        </div>
    )
}

export default DescriptionForm
