import React, {useState, useEffect} from 'react'
import Note from '../Note'
import './board.css'
import axios from 'axios'

function Board() {

    const [board, setBoard] = useState()
    const [noteTitle, setNoteTitle] = useState()

    const fetchNotes = async () => {
        const notes = await axios.get('/notes')
        setBoard(notes.data)
    }
    

    useEffect(() => {
        fetchNotes()
    }, [])


    const createNote = async () => {
        const note = {
            title: noteTitle
        } 
        setBoard([...board, note])
        await axios.post('/notes', note)
        fetchNotes();
        
    }

    console.log(board)    

    const handleAddNote = () => {
        return(
            <div>
                <input onChange = {e => setNoteTitle(e.target.value) } />
                <button onClick = {createNote} >add</button>
            </div>
        )
    }

   

    const renderNotes = () => {
        return(
            <div>
                { board && board.map( note => <Note board = {board} setBoard = {setBoard} note = {note} /> )}
            </div>
        )
    }


    return (
        <div className = 'board' >
            {renderNotes()}
            {handleAddNote()}

            
        </div>
    )
}

export default Board
